<?php

namespace AppBundle\Repository;

/**
 * CategorieChambreRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CategorieChambreRepository extends \Doctrine\ORM\EntityRepository
{
    public function list($agence)
    {
        $em = $this->getEntityManager();
        
        $query = "  select c.id, c.nom, t.nom as type, c.caracteristiques
                    from categorie_chambre c
                    inner join type_chambre t on (c.type_chambre = t.id)
                    where c.nom is not null ";

        if ($agence) {
            $query .= " and c.agence = " . $agence ;
        }

        $query .= " order by c.nom asc";

        $statement = $em->getConnection()->prepare($query);

        $statement->execute();

        $result = $statement->fetchAll();

        return $result;
    }
}
